class A:    def do(self):        print("ln C")class B(A):    passclass C:    def do(self):        print("ln A")class D(B, C):    passf = D()f.do()#print(D.mro())print("________________________________________________________")class User:  # Super main parent base    def __init__(self, name, age):        print(f"welcome {name}")        self.name = name        self.age = age    def show_details(self):        print(f"name: {self.name}")        print(f"age: {self.age}")class Bank(User): # child sub derived     def __init__(self, name, age):        super().__init__(name, age) #تشغيل الكونستركتور بالوراثه        self.balance = 0    def deposit(self, amount):        self.balance += amount        print(f"your balance is {self.balance}")    def withdraw(self, amount):        if amount > self.balance:            print(f" رصيدك غير كافي وهو :{self.balance}")            return        self.balance -= amount        print(f"your balance is {self.balance}")    def show_balance(self):        print(f"your balance is {self.balance}")cA = Bank("Sherief Masood", 28)print("_____________________الايداع_____________________________")cA.deposit(1000)print("____________________السحب_______________________________")cA.withdraw(500)print("____________________السحب_______________________________")cA.withdraw(600)print("________________________________________________________")cA.show_details()print("________________________________________________________")